import React, { useState, useEffect } from "react";
import Select from "react-select"; // Import React-Select
import ButtonDanger from "./ButtonDanger"; // Import ButtonDanger component
import image5 from "./image-5.png"; // Import images
import f1LapSimulator from "./Image0.png";
import image6 from "./image-6.png";
import image7 from "./image-7.png";
import rectangle6 from "./rectangle-6.svg"; // Import rectangle-6.svg
import rectangle5 from "./rectangle-5.png"; // Import rectangle-5.png
import "./style.css"; // Import styles

export const LandingPage = () => {
  const [drivers, setDrivers] = useState([]);
  const [constructors, setConstructors] = useState([]);
  const [circuits, setCircuits] = useState([]);
  const [selectedDriver, setSelectedDriver] = useState(null);
  const [selectedConstructor, setSelectedConstructor] = useState(null);
  const [selectedCircuit, setSelectedCircuit] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch dropdown data from the backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const driversResponse = await fetch("http://127.0.0.1:5000/drivers");
        const constructorsResponse = await fetch("http://127.0.0.1:5000/constructors");
        const circuitsResponse = await fetch("http://127.0.0.1:5000/circuits");

        const driversData = await driversResponse.json();
        const constructorsData = await constructorsResponse.json();
        const circuitsData = await circuitsResponse.json();

        // Map the data to React-Select options
        setDrivers(driversData.map((driver) => ({ value: driver.driverId, label: driver.driverRef })));
        setConstructors(constructorsData.map((constructor) => ({ value: constructor.constructorId, label: constructor.constructorRef })));
        setCircuits(circuitsData.map((circuit) => ({ value: circuit.circuitId, label: circuit.circuitRef })));
      } catch (err) {
        console.error("Failed to fetch dropdown data:", err);
      }
    };

    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      const response = await fetch("http://127.0.0.1:5000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          input: [selectedDriver.value, selectedConstructor.value, selectedCircuit.value],
        }),
      });

      if (!response.ok) {
        throw new Error("Failed to fetch prediction");
      }

      const data = await response.json();
      setPrediction(data.prediction);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="landing-page">
      <div className="overlap-wrapper">
        <div className="overlap">
          <div className="overlap-group">
            {/* Navigation Section */}
            <div className="navigation">
              <div className="div">
                <div className="text-wrapper">f1-lap-predictor</div>

                {/* White background surrounding image-5 */}
                <div className="image-wrapper">
                  <div className="image-5-background">
                    <img
                      className="image-5-button"
                      alt="Reset App"
                      src={image5}
                      onClick={() => window.location.reload()}
                      style={{ cursor: "pointer" }}
                    />
                  </div>
                </div>
              </div>
            </div>

            {/* Dropdowns Section */}
            <div className="dropdowns-container">
              <Select
                className="dropdown"
                options={drivers}
                value={selectedDriver}
                onChange={setSelectedDriver}
                placeholder="Select Driver ID"
              />
              <Select
                className="dropdown"
                options={constructors}
                value={selectedConstructor}
                onChange={setSelectedConstructor}
                placeholder="Select Constructor ID"
              />
              <Select
                className="dropdown"
                options={circuits}
                value={selectedCircuit}
                onChange={setSelectedCircuit}
                placeholder="Select Circuit ID"
              />
            </div>

            {/* Prediction Section */}
            <form onSubmit={handleSubmit}>
              <button type="submit" className="predict-button">
                Predict
              </button>
            </form>

            {loading && <p>Loading...</p>}
            {prediction && (
              <div>
                <h2>Prediction</h2>
                <img className="rectangle-6" alt="Output Display" src={rectangle6} />
                <p>{prediction[0]}</p>
              </div>
            )}
            {error && (
              <div>
                <h2>Error</h2>
                <p>{error}</p>
              </div>
            )}

            {/* Additional Buttons */}
            <div className="buttons-container">
              <ButtonDanger />
              <img className="image-6" alt="Info Button" src={image6} />
              <img className="image-7" alt="Help Button" src={image7} />
            </div>

            {/* Footer Section */}
            <div className="footer">
              <img className="rectangle-5" alt="Footer Background" src={rectangle5} />
              <p>Powered by F1 Lap Predictor</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
